import { Languages } from "../types/Languages";

export const regEmail = /^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/i;
export const regName = /^[a-zA-Z]+$/;
export const regPassword = /^(?=.*[0-9])(?=.*[!@#$%^&*])[a-zA-Z0-9!@#$%^&*]{6,16}$/;


export const initValues = {
    items: [{
        image: '',
        title: '',
        code: 0,
        country: '',
        year: 0,
        rating: 0,
        price: '',
    }],
    loading: false,
    filter: '',
    formik: {
        firstName: '',
        lastName: '',
        email: '',
        password: '',
    },
    lang: Languages.ENG,
}
